//! ポケモンのデータを保持する。

use std::collections::HashMap;
use std::sync::OnceLock;

use crate::pokemon::Stats;
use crate::moves::*;
use crate::types::{Type, TYPES};

#[derive(Debug)]
pub struct Pokepedia {
    no: &'static str,
    name: &'static str,
    type1: Type,
    type2: Option<Type>,

    // 種族値
    base_stats: Stats,

    // 技
    fast_moves: &'static [&'static FastMove],
    charge_moves: &'static [&'static ChargeMove],
}

impl Pokepedia {
    pub fn no(&self) -> &'static str {
        self.no
    }

    pub fn name(&self) -> &'static str {
        self.name
    }

    pub fn types(&self) -> Vec<Type> {
        if let Some(type2) = self.type2 {
            vec![self.type1, type2]
        } else {
            vec![self.type1]
        }
    }

    pub fn base_stats(&self) -> Stats {
        self.base_stats
    }

    pub fn fast_moves(&self) -> &'static [&'static FastMove] {
        self.fast_moves
    }

    pub fn charge_moves(&self) -> &'static [&'static ChargeMove] {
        self.charge_moves
    }

    pub fn print_effect_bonus(&self) {
        let types = self.types();

        for t in TYPES {
            let name: String = String::from(t);
            let num_spaces = 10 - (name.len() / 3 * 2);
            let effect = t.type_effect_bonus(&types);

            println!("{} {:<width$}: {:.2}", name, "", effect, width=num_spaces);
        }
    }
}

/// ポケモンNoからポケモン情報を取得するマップを返す。
#[allow(dead_code)]
pub fn pokepedia_by_no(no: &str) -> Option<&'static Pokepedia> {
    let m = POKEPEDIA_NO_MAP.get_or_init(|| {
        let mut m = HashMap::with_capacity(NUM_POKEPEDIA);

        for p in &POKEPEDIA {
            m.insert(p.no.to_string(), p);
        }

        m
    });

    match m.get(no) {
        None => None,
        Some(p) => Some(*p)
    }
}

#[test]
fn test_pokepedia_by_no() {
    let p = pokepedia_by_no("0909").unwrap();
    assert_eq!(p.name, "ホゲータ");
}

/// ポケモン名からポケモン情報を取得するマップを返す。
pub fn pokepedia_by_name(name: &str) -> Option<&'static Pokepedia> {
    let m = POKEPEDIA_NAME_MAP.get_or_init(|| {
        let mut m = HashMap::with_capacity(NUM_POKEPEDIA);

        for p in &POKEPEDIA {
            m.insert(p.name.to_string(), p);
        }

        m
    });

    match m.get(name) {
        None => None,
        Some(p) => Some(*p)
    }
}

#[test]
fn test_pokepedia_by_name() {
    let p = pokepedia_by_name("ホゲータ").unwrap();
    assert_eq!(p.no, "0909");
}

/// noとnameが一意(ユニーク)であるかをチェックする
#[test]
pub fn test_pokepedia_uniq() {
    use std::collections::HashSet;

    let mut no_set = HashSet::with_capacity(NUM_POKEPEDIA);
    let mut name_set = HashSet::with_capacity(NUM_POKEPEDIA);

    for p in &POKEPEDIA {
        if !no_set.insert(p.no.to_string()) {
            panic!("pokepediaの番号にダブりあり: {}", p.no);
        }

        if !name_set.insert(p.name.to_string()) {
            panic!("pokepediaの名前にダブりあり: {}", p.name);
        }
    }
}

